  error VAULT_VALUE_CHANGE_TOO_HIGH();
  error FINAL_VAULT_RATIO_TOO_LOW(uint256 minRatio, uint256 actualRatio);
  error VAULT_NOT_AUTOMATED();
  error VAULT_TRIGGER_RATIO_NOT_REACHED(uint256 actual, uint256 expected);
